// Cell phone portrait mode
@include xs {

  .header {

    &__logo {
      transform: scale(.2.5);
      margin-top: -11.5rem;
    }
  }

  .card {
    width: 30rem;
    max-width: 40rem;
    width: 100%;
    margin-bottom: 22rem;

    &:not(.card:last-child) {
      margin-bottom: 7rem;
    }

    &__image-logo {
      position: absolute;
      top: 0;
      left: 50%;
      transform: translateX(-50%) scale(.5);
    }

    &__back {
      height: 86rem;
    }

    &__front {
      height: 86rem;
    }
  }

  .btn {
    position: absolute;
    bottom: 15%;
    left: 50%;
    transform: translateX(-50%);
  }

  .testimonials {
    margin-top: -2rem;
  }
}

// Cell phone landscape mode 
@include sm {

}

// Tablets and small laptops
@include md {
  
  .nav__links {
    left: 50%;
    transform: translate(-50%, -6rem);
    width: 60rem;
    height: auto;
    padding: 1rem 2rem;
    background: none;
  }

  .nav__links {

  
    ul {
      flex-direction: row;
  
      li {
        list-style: none;
        margin: 0 1.6rem;
        font-size: 2rem;
        font-weight: bold;
        line-height: 3.6rem;
        text-shadow: 0 .6rem .6rem $color-black;
        border-bottom: none;
        @include transition-fast;

        &:hover {
          background-color: rgba(0, 0, 0, .1);
          padding: .2rem .8rem;
          border-radius: 3px;
        }
  
        a {
          text-decoration: none;
          color: $color-white;
        
          :link,
          :visited {
            color: $color-white;
          }
        }
      }
    }
  
    &.drop-down {
      left: 50%;
      transform: translate(-50%, -2%);
    }
  }

  .header {

    .nav {
      top: 3%;
      right: 1%;
    }

  }
  
  .testimonials {
    clip-path: polygon(0 4%, 100% 0, 100% 96%, 0 100%);

    &__heading {
      margin-top: 4rem;
    }
  }
}

// Laptops and small televisions
@include lg {

  h2 {
    font-size: 3.4rem;
  }

  .header {

    &__logo {
      transform: scale(.5);
      margin: -8rem 0 0 -12rem;
    }
  }

